import React, { Component } from 'react'

class About extends Component {
    render() {
        return (
            <div className="about-container">
                <section className="about-description">
                    <h4>ABOUT THIS PAGE</h4>

                    <h4>HOW DID WE DO IT?</h4>
                    1) We have filtered through The Guardian's latest articles mentioning tech and computing <br></br>
                    2) Used a gender detection npm package to detect female names <br></br>
                    3) Displayed the resulting female authors of the articles on this page, pulling in their name, photo, biography and a link to their articles.
                </section>
                <br></br>
                <p>Created with love, React & Express by <br></br> <a href="https://www.linkedin.com/in/carolinejold/" target="_blank">Caroline Old</a></p>

            </div>
        )
    }
}

export default About;
