import React, { Component } from 'react'

class About extends Component {
    render() {
        return (
            <div className="about-container">
                <p className="about-description">
                    1) We have filtered through The Guardian's latest articles mentioning tech and computing <br></br>
                    2) Used a gender detection package to detect female names
                    3) Displayed the resulting female authors of the articles on this page.
                </p>
                <br></br>
                <p>Created with React & Express by <br></br> <a href="https://www.linkedin.com/in/carolinejold/" target="_blank">Caroline Old</a></p>

            </div>
        )
    }
}

export default About;
