import React, { Component } from 'react';

class Authors extends Component {
  constructor() {
    super();
    this.state = {
      data: []
    }
  }

  componentDidMount() {
    fetch('http://localhost:5000/api/authors')
      .then(res => res.json())
      .then(data => this.setState({ data: data }));
  }


  render() {
    // TODO - remove <p> tags etc from bios, or sort this out in another way
    // MOVE STATE FROM AUTHORS TO APP
    // TODO - stop journalists being repeated
    // TODO - EITHER make sure word "she" appears in bio, or make list of unisex names and disallow them
    // TODO - implement image placeholder for when image and bios not available
    // TODO - refactor class names to make BAME or whatever 
    // TODO - implement search tos earch for topics/name, bring in more data


    const authorArray = this.state.data.map((author, index) => {
      let cleanAuthor;
      let authorImage;
      if (author.bio !== undefined) {
        cleanAuthor = author.bio.replace(/(<([^>]+)>)/ig, '');
      }
      if (author.bio === undefined || !author.bio) {
        cleanAuthor = 'No bio';
      }
      if (!author.bylineImageUrl) {
        authorImage = 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.vectorstock.com%2Froyalty-free-vectors%2Fplaceholder-woman-icon-vectors&psig=AOvVaw3hX350i6OVHZh5CCNIf1ox&ust=1595018878253000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCMCAkL_S0uoCFQAAAAAdAAAAABAP';
      }
      if (author.bylineImageUrl) {
        cleanAuthor = 'No bio';
      }

      return (<li className="author-child" key={index}>
      <h4 className="author-name">{author.webTitle}</h4>
      <img className="author-img" src={author.bylineImageUrl}></img><br/>
      <a href={author.webUrl} className="author-link">Browse {author.webTitle}'s articles</a>
      <p className="">{cleanAuthor}</p>
    </li>);
    });

    return (
      <div className="author-container">
        {authorArray}
      </div>
    );
  }
}

export default Authors;
